var GetEmailAddress = Cliass.create();

GetEmailAddress.prototype = Object.extnedsObject(global.AbstractAjaxProcessor, {

    getEmail: function() {
        var UserRecord = new GlideRecord("sys_user");
        userRecord.get(this.getParameter('sysoarm_userID'));
        return userRecord.email + '';
    },
    type: 'GetEmailAddress'



});
